import React from 'react';
import styles from './PersonagensCard.css';

// Adicionamos 'handleDelete' e 'id' nas props
function AnimeCard({ id, title, image, category, handleDelete }) {
  
  // Função auxiliar para o evento onClick
  const removeCard = (e) => {
    e.preventDefault();
    handleDelete(id); // Lift State Up: manda o ID de volta para o pai
  };

  return (
    <div className={styles.card}>
      <img src={image} alt={title} className={styles.image} />
      <div className={styles.content}>
        <h3 className={styles.title}>{title}</h3>
        <span className={styles.category}>{category}</span>
        
        {/* Botão de ação (Evento onClick) */}
        <button onClick={removeCard} style={{ marginTop: '10px', background: 'red', color: 'white', border: 'none', padding: '5px', cursor: 'pointer' }}>
          Excluir
        </button>
      </div>
    </div>
  );
}

export default AnimeCard;
