import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/Home.js';
import Cadastro from './Pages/Cadastro.js/index.js'; // Vamos criar já já
// Importaremos detalhes depois para não dar erro agora

import styles from './App.css'; // Crie um css simples para o menu

function App() {
  return (
    <Router>
      {/* Menu de Navegação Simples */}
      <nav style={{ background: '#2c3e50', padding: '15px', marginBottom: '20px' }}>
        <Link to="/" style={{ color: 'white', marginRight: '15px', textDecoration: 'none' }}>Home</Link>
        <Link to="/cadastro" style={{ color: 'white', textDecoration: 'none' }}>Novo Personagem</Link>
      </nav>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/cadastro" element={<Cadastro />} />
        {/* Rota de Detalhes (Faremos em seguida) */}
        {/* <Route path="/detalhes/:id" element={<Detalhes />} /> */}
      </Routes>
    </Router>
  );
}

export default App;
