import React, { useState, useEffect } from 'react';
// import axios from 'axios'; // Mantenha comentado até ter o backend, ou use para JSON local
import PersonagensCard from '../components/PersonagensCard';
import styles from './Home.css';

function Home() {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true); // Estado para renderização condicional

  useEffect(() => {
    // Simulando uma requisição HTTP (aqui entraria o axios.get)
    setTimeout(() => {
        setCharacters([
            { id: 1, name: 'Naruto Uzumaki', category: 'Ninja', image: 'https://cdn.myanimelist.net/images/characters/9/131317.jpg' },
            { id: 2, name: 'Monkey D. Luffy', category: 'Pirata', image: 'https://cdn.myanimelist.net/images/characters/9/310307.jpg' },
        ]);
        setLoading(false);
    }, 1000);
  }, []);

  // Função Lift State Up: O filho chama, o pai executa
  function removeCharacter(id) {
    // Filtra a lista removendo o item com o ID recebido
    setCharacters(characters.filter(char => char.id !== id));
    console.log(`Item ${id} removido!`);
  }

  return (
    <div className={styles.container}>
      <h1 className={styles.header}>Personagens</h1>

      {/* Renderização Condicional */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <div className={styles.grid}>
          {characters.length > 0 ? (
            characters.map((char) => (
              <PersonagensCard 
                key={char.id}
                id={char.id}
                title={char.name} 
                image={char.image} 
                category={char.category}
                handleDelete={removeCharacter} // Passando a função para o filho
              />
            ))
          ) : (
             <p>Nenhum personagem encontrado.</p> // Outra condicional
          )}
        </div>
      )}
    </div>
  );
}

export default Home.js;
