import React, { useState, useEffect } from 'react';
// CORREÇÃO: Importando o componente com o nome novo que você deu
import PersonagensCard from '../components/PersonagensCard'; 
// import styles from './Home.module.css'; // Comente se der erro de estilo

function Home() {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulação de dados
    setTimeout(() => {
        setCharacters([
            { id: 1, name: 'Exemplo 1', category: 'Teste', image: 'https://via.placeholder.com/150' },
            { id: 2, name: 'Exemplo 2', category: 'Teste', image: 'https://via.placeholder.com/150' },
        ]);
        setLoading(false);
    }, 1000);
  }, []);

  function removeCharacter(id) {
    setCharacters(characters.filter(char => char.id !== id));
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>Lista de Personagens</h1>
      {loading ? <p>Carregando...</p> : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {characters.map((char) => (
            // Usando o componente PersonagensCard
            <PersonagensCard 
              key={char.id}
              id={char.id}
              title={char.name} 
              image={char.image} 
              category={char.category}
              handleDelete={removeCharacter} 
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
