import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Importando a biblioteca de conexão
import PersonagensCard from '../components/PersonagensCard'; 

function Home() {
  const [personagens, setPersonagens] = useState([]);
  const [loading, setLoading] = useState(true);
  const [erro, setErro] = useState(null);

  // 1. Buscar dados do Banco (GET)
  useEffect(() => {
    axios.get('http://localhost:5000/personagens')
      .then((response) => {
        setPersonagens(response.data); // Guarda os dados do banco no estado
        setLoading(false);
      })
      .catch((error) => {
        console.error("Erro ao buscar dados:", error);
        setErro("Erro ao conectar com o servidor. O Backend está ligado?");
        setLoading(false);
      });
  }, []);

  // 2. Deletar um item (DELETE)
  function removeCharacter(id) {
    // Primeiro deleta no banco
    axios.delete(`http://localhost:5000/personagens/${id}`)
      .then(() => {
        // Se der certo, remove da tela visualmente
        setPersonagens(personagens.filter(char => char._id !== id));
        alert('Personagem excluído com sucesso!');
      })
      .catch((error) => {
        console.error("Erro ao deletar:", error);
        alert("Erro ao tentar excluir.");
      });
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>Lista de Personagens</h1>
      
      {/* Exibição de Erro, se houver */}
      {erro && <p style={{ color: 'red' }}>{erro}</p>}

      {loading ? <p>Carregando...</p> : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {personagens.length > 0 ? (
            personagens.map((char) => (
              <PersonagensCard 
                key={char._id} // O Mongo usa _id com underline
                id={char._id}
                title={char.name} 
                image={char.image} 
                category={char.category}
                handleDelete={removeCharacter} 
              />
            ))
          ) : (
            <p>Nenhum personagem cadastrado ainda.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Home;
