import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

import Home from './Pages/Home';
import Cadastro from './Pages/Cadastro';
import Editar from './Pages/Editar'; // Importando a nova p치gina

function App() {
  return (
    <BrowserRouter>
      <nav style={{ background: '#2c3e50', padding: '15px', color: '#fff', marginBottom: '20px', display: 'flex', gap: '20px', justifyContent: 'center' }}>
        <Link to="/" style={{ color: '#fff', textDecoration: 'none', fontWeight: 'bold' }}>HOME (Visualizar)</Link>
        <Link to="/cadastro" style={{ color: '#fff', textDecoration: 'none', fontWeight: 'bold' }}>NOVO PERSONAGEM (Cadastrar)</Link>
      </nav>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/cadastro" element={<Cadastro />} />
        {/* Nova Rota: O :id significa que vai receber um c칩digo vari치vel */}
        <Route path="/editar/:id" element={<Editar />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
