import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

// Importando as páginas
import Home from './Pages/Home';
import Cadastro from './Pages/Cadastro';
import Editar from './Pages/Editar';

// Importando o CSS bonito que acabamos de criar
import styles from './App.module.css';

function App() {
  return (
    <BrowserRouter>
      {/* HEADER ESTILIZADO */}
      <header className={styles.header}>

        {/* LOGO FAKE */}
        <div className={styles.logo}>
          ANIME<span style={{ fontSize: '1.2rem', color: '#fff', opacity: 0.8 }}>HUB</span>
        </div>

        {/* NAVEGAÇÃO */}
        <nav className={styles.nav}>
          <Link to="/" className={styles.link}>
            LISTA DE ANIMES
          </Link>

          {/* Destaque para o botão de cadastro */}
          <Link to="/cadastro" className={`${styles.link} ${styles.activeLink}`}>
            + NOVO PERSONAGEM
          </Link>
        </nav>

      </header>

      {/* CONTEÚDO DA PÁGINA */}
      <div style={{ padding: '20px', minHeight: 'calc(100vh - 80px)', backgroundColor: '#f4f4f9' }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/cadastro" element={<Cadastro />} />
          <Route path="/editar/:id" element={<Editar />} />
        </Routes>
      </div>

    </BrowserRouter>
  );
}

export default App;
